import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

const Index = () => {
  let num = 0
  const numchanger = () => {
    console.log(num)

    num >= foodmenu.length ? (num = 0) : num++
  }

  setInterval(numchanger, 1000)

  return (
    <div className="index">
      <div className="index-slider">
        {/* foodmenu.map(item => (
          <IndexSlider link={item.img} alt={item.alt} />
        )) */}
        <div className="slider-hero">
          <IndexSlider
            link={
              foodmenu[
                setInterval(function() {
                  return 3
                }, 1000)
              ].img
            }
          />
        </div>
      </div>
      <div className="index-menu">
        <IndexMenu />
      </div>
    </div>
  )
}

export default Index
