import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

class Index extends React.Component {
  state = { img: "" }
  num = 0
  numchanger = () => {
    this.setState({ img: "" })
    this.setState({ img: foodmenu[this.num].img })
    if (this.num >= foodmenu.length) {
      this.num = 0
    }

    this.num++
  }
  changeNum = () => {
    setInterval(this.numchanger, 5000)
  }

  render() {
    this.changeNum()
    return (
      <div className="index">
        <div className="index-slider">
          {foodmenu.map(item => (
            <IndexSlider link={item.img} alt={item.alt} />
          ))}
          <IndexSlider link={this.state.img} alt="Img" />
          <div className="slider-hero" />
        </div>

        <div className="index-menu">
          <IndexMenu />
        </div>
      </div>
    )
  }
}

export default Index
