import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

class Index extends React.Component {
  state = { img: "" }
  render() {
    let index = 0
    const newState = { ...this.state }
    const max = foodmenu.length - 1
    const number = () => {
      if (index >= max) {
        index = 0
        return index
      } else {
        index++
        return index
      }
    }
    setInterval(function() {
      console.log(number())
    }, 3000)

    return (
      <div className="index">
        <div className="index-slider">
          <div id="slider">
            <figure>
              {foodmenu.map(item => (
                <img src={item.img} />
              ))}
              <img src={foodmenu[0].img} />
            </figure>
          </div>
          <div className="slider-hero" />
        </div>

        <div className="index-menu">
          <IndexMenu link="" heading="VEG" />
        </div>
      </div>
    )
  }
}

export default Index
