import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import Video from "../utlis/Videos.mp4"
import { foodmenu } from "../utlis/data"

class Index extends React.Component {
  state = { foodmenu: [] }
  componentDidMount() {
    this.setState({ foodmenu: foodmenu })
  }
  render() {
    let index = 0
    const newState = { ...this.state }
    const max = foodmenu.length - 1
    const number = () => {
      if (index >= max) {
        index = 0
        return index
      } else {
        index++
        return index
      }
    }
    setInterval(function() {
      console.log(number())
    }, 3000)

    return (
      <div className="index">
        <div className="index-slider">
          <div id="slider">
            <figure>
              {this.state.foodmenu.map(item => (
                <img src={item.img} />
              ))}
            </figure>
          </div>
          <div className="slider-hero" />
        </div>

        <div className="index-menu">
          <div className="heading">
            <h1>Our Menu</h1>
          </div>
          <IndexMenu link={this.state.foodmenu[0].img} heading="Breakfast" />
          <IndexMenu link={this.state.foodmenu[3].img} heading="LUNCH" />
          <IndexMenu link={this.state.foodmenu[2].img} heading="DINNER" />
        </div>
      </div>
    )
  }
}

export default Index
