import axios from "axios"
import {
  GET_PROFILE,
  PROFILE_LOADING,
  CLEAR_CURRENT_PROFILE,
  GET_ERRORS,
  SET_CURRENT_USER
} from "./types"

//Get current profile
export const getCurrentProfile = () => dispatch => {
  dispatch(setProfileLoading())
  axios.get("/api/profile").then(res =>
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    })
  )
}
//create profile
