import React, { Component } from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"
import ImageSilder from "./ImageSilder"

class Index extends Component {
  ImageSilder = () => {
    let index = 1
    if (index >= foodmenu.length) {
      index = 0
    }
  }
  render() {
    return (
      <div className="index">
        <div className="index-slider">
          {foodmenu.map(item => (
            <IndexSlider link={item.img} alt={item.alt} />
          ))}
          <div className="slider-hero">
            <IndexSlider link={foodmenu[0].img} />
          </div>
        </div>
        <div className="index-menu">
          <IndexMenu />
        </div>
      </div>
    )
  }
}
export default Index
