import React, { Component } from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

const Index = () => {
  let index = 0
  const indexChanger = () => {
    if ((index = foodmenu.length)) {
      index = 0
    }
    index++
    return index
  }
  return (
    <div className="index">
      <div className="index-slider">
        {foodmenu.map(item => (
          <IndexSlider link={item.img} alt={item.alt} />
        ))}
        <div className="slider-hero">
          <IndexSlider link={foodmenu[setInterval(indexChanger, 2000)].img} />
        </div>
      </div>
      <div className="index-menu">
        <IndexMenu />
      </div>
    </div>
  )
}

export default Index
