import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

class Index extends React.Component {
  state = { img: "" }
  slideIndex = 0
  showSlides = () => {
    for (let i = 0; i < foodmenu.length; i++) {
      this.setState({img:foodmenu[i]})
    }
    this.slideIndex++
    if (this.slideIndex > slides.length) {
      this.slideIndex = 1
    }
    slides[this.slideIndex - 1].style.display = "block"
    setTimeout(this.showSlides, 2000) // Change image every 2 seconds
  }

  render() {
    this.showSlides()
    return (
      <div className="index">
        <div className="index-slider">
          {foodmenu.map(item => (
            <IndexSlider link={item.img} alt={item.alt} />
          ))}
          <IndexSlider link={this.state.img} alt="Img" />
          <div className="slider-hero" />
        </div>

        <div className="index-menu">
          <IndexMenu link="" heading="VEG" />
        </div>
      </div>
    )
  }
}

export default Index
