import React, { Component } from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

const Index = () => {
  let num = 0
  const indexChnager = () => {
    num = foodmenu.length ? (num = 0) : num
    num++
  }
  const changeNum = () => {
    setInterval(indexChnager, 1000)
  }
  return (
    <div className="index">
      <div className="index-slider">
        {/* foodmenu.map(item => (
          <IndexSlider link={item.img} alt={item.alt} />
        )) */}
        <div className="slider-hero">
          <IndexSlider link={foodmenu[setInterval(changeNum())].img} />
        </div>
      </div>
      <div className="index-menu">
        <IndexMenu />
      </div>
    </div>
  )
}

export default Index
