import React from "react"
import "./footer.scss"

const Footer = () => {
  const subscribe_button = document.querySelector(".subscribe_button")
  subscribe_button.addEventListener("click", function() {
    var subscribing = document.querySelector(".subscribing")
    var thanks = document.querySelector(".thanks")
    var login = document.querySelector(".login")

    subscribing.classList.add("subscribing_active")
    subscribe_button.classList.add("subscribe_button_active")
    setTimeout(function() {
      login.classList.add("login_active")
    }, 1200)
    setTimeout(function() {
      thanks.classList.add("thanks_active")
    }, 1400)

    setTimeout(function() {
      thanks.classList.remove("thanks_active")
      login.classList.remove("login_active")
      subscribing.classList.remove("subscribing_active")
      subscribe_button.classList.remove("subscribe_button_active")
    }, 4000)
  })
  return (
    <div className="footer">
      <div className="footer-dot">
        <h1>FUDO</h1>
      </div>

      <div className="copyright">
        &copy;anujthapa {new Date().getFullYear()}
      </div>
      <div className="socialmedia">facebook,insta</div>
      <div className="subscribe">
        <div className="login_bar">
          <span className="login">super-email@gmail.com</span>
          <span className="subscribe_button" onclick="subscribeOnClick()">
            subscribe
          </span>
          <span className="subscribing" />
          <span className="thanks"> Thank you. You have been subscribed</span>
        </div>
      </div>
    </div>
  )
}
export default Footer
