import React from "react"
import IndexSlider from "./ImageSilder"
import IndexMenu from "./Indexmenu"
import "./index.scss"
import { foodmenu } from "../utlis/data"

class Index extends React.Component {
  state = { img: "" }
  render() {
    let index = 0
    const ImageChanger = () => {
      setInterval(function() {
        index >= foodmenu.length - 1 ? (index = 0) : index++
      }, 2000)
      this.setState({ img: index })
      return index
    }


    return (
      <div className="index">
        <div className="index-slider">
          {foodmenu.map(item => (
            <IndexSlider link={item.img} alt={item.alt} />
          ))}
          <IndexSlider link={foodmenu[this.state.img]} alt="Img" />
          <div className="slider-hero" />
        </div>

        <div className="index-menu">
          <IndexMenu />
        </div>
      </div>
    )
  }
}

export default Index
